$((function(){const t=$(".categories"),e=$(".results");t.change((function(){let t=event.target.value;e[0].innerHTML="",$.ajax({method:"GET",url:`https://api.nytimes.com/svc/topstories/v2/${t}.json?api-key=tGGrhXgAGkkUeGlxub1PQ7smASFvNVgP`}).done((function(t){t.results.filter((function(t){if(null!==t.multimedia)return t})).slice(0,12).forEach(t=>{let n=`\n                <a href="${t.url}" target="_blank">\n                  <li class='article' style="background-image: url(${t.multimedia[0].url})">  \n                    <p>${t.abstract}</p>\n                  </li>\n                </a>`;e.append(n)})}))}))}));