document.addEventListener("DOMContentLoaded",(function(){const e=document.querySelector(".categories"),t=$(".results");e.addEventListener("change",()=>{let e=event.target.value;t[0].innerHTML="",$.ajax({method:"GET",url:`https://api.nytimes.com/svc/topstories/v2/${e}.json?api-key=tGGrhXgAGkkUeGlxub1PQ7smASFvNVgP`}).done((function(e){e.results.forEach(e=>{t.append(`\n          <a href="${e.url}" target="_blank">\n            <li class='article' style="background-image: url(${e.multimedia[0].url})">  \n              <p>${e.abstract}</p>\n            </li>\n          </a>`),t.length>12&&t.splice(0,12)})}))})}));